mapper.select:
SELECT COALESCE(ib.码头, lo.berthing_location) as berthingLocation, COUNT(*) as shipCount FROM ship_port_log lo LEFT JOIN international_berth ib ON ib.泊位 = lo.berthing_location WHERE lo.inspection_datetime >= #{startDate} AND lo.inspection_datetime <= CONCAT(#{endDate}, ' 23:59:59') AND lo.berthing_location IS NOT NULL AND lo.berthing_location != ''
AND ib.码头 = #{berthingLocation}
AND lo.ship_category = #{shipCategory}
GROUP BY COALESCE(ib.码头, lo.berthing_location) ORDER BY shipCount DESC

SELECT COALESCE(ib.码头, lo.berthing_location) as berthingLocation, COALESCE(SUM(lo.loaded_unloaded_cargo_tonnage), 0) as cargoTonnage FROM ship_port_log lo LEFT JOIN international_berth ib ON ib.泊位 = lo.berthing_location WHERE lo.inspection_datetime >= #{startDate} AND lo.inspection_datetime <= CONCAT(#{endDate}, ' 23:59:59') AND lo.berthing_location IS NOT NULL AND lo.berthing_location != ''
AND ib.码头 = #{berthingLocation}
AND lo.ship_category = #{shipCategory}
GROUP BY COALESCE(ib.码头, lo.berthing_location) ORDER BY cargoTonnage DESC

SELECT lo.ship_category as shipCategory, COUNT(*) as shipCount FROM ship_port_log lo LEFT JOIN international_berth ib ON ib.泊位 = lo.berthing_location WHERE lo.inspection_datetime >= #{startDate} AND lo.inspection_datetime <= CONCAT(#{endDate}, ' 23:59:59') AND lo.ship_category IS NOT NULL AND lo.ship_category != ''
AND ib.码头 = #{berthingLocation}
AND lo.ship_category = #{shipCategory}
GROUP BY lo.ship_category ORDER BY shipCount DESC

SELECT lo.previous_next_port as portName, COUNT(*) as shipCount FROM ship_port_log lo LEFT JOIN international_berth ib ON ib.泊位 = lo.berthing_location WHERE lo.inspection_datetime >= #{startDate} AND lo.inspection_datetime <= CONCAT(#{endDate}, ' 23:59:59') AND lo.previous_next_port IS NOT NULL AND lo.previous_next_port != ''
AND ib.码头 = #{berthingLocation}
AND lo.ship_category = #{shipCategory}
GROUP BY lo.previous_next_port HAVING COUNT(*) > 2 ORDER BY shipCount DESC

SELECT lo.previous_next_port as portName, COALESCE(SUM(lo.loaded_unloaded_cargo_tonnage), 0) as cargoTonnage FROM ship_port_log lo LEFT JOIN international_berth ib ON ib.泊位 = lo.berthing_location WHERE lo.inspection_datetime >= #{startDate} AND lo.inspection_datetime <= CONCAT(#{endDate}, ' 23:59:59') AND lo.previous_next_port IS NOT NULL AND lo.previous_next_port != ''
AND ib.码头 = #{berthingLocation}
AND lo.ship_category = #{shipCategory}
GROUP BY lo.previous_next_port HAVING COUNT(*) > 2 ORDER BY cargoTonnage DESC

SELECT lo.port_of_registry as registryPort, COUNT(*) as shipCount FROM ship_port_log lo LEFT JOIN international_berth ib ON ib.泊位 = lo.berthing_location WHERE lo.inspection_datetime >= #{startDate} AND lo.inspection_datetime <= CONCAT(#{endDate}, ' 23:59:59') AND lo.port_of_registry IS NOT NULL AND lo.port_of_registry != ''
AND ib.码头 = #{berthingLocation}
AND lo.ship_category = #{shipCategory}
GROUP BY lo.port_of_registry HAVING COUNT(*) >= 2 ORDER BY shipCount DESC

SELECT lo.ship_owner as ownerName, COUNT(*) as shipCount FROM ship_port_log lo LEFT JOIN international_berth ib ON ib.泊位 = lo.berthing_location WHERE lo.inspection_datetime >= #{startDate} AND lo.inspection_datetime <= CONCAT(#{endDate}, ' 23:59:59') AND lo.ship_owner IS NOT NULL AND lo.ship_owner != ''
AND ib.码头 = #{berthingLocation}
AND lo.ship_category = #{shipCategory}
GROUP BY lo.ship_owner HAVING COUNT(*) >= 2 ORDER BY shipCount DESC

SELECT lo.ship_operator as operatorName, COUNT(*) as shipCount FROM ship_port_log lo LEFT JOIN international_berth ib ON ib.泊位 = lo.berthing_location WHERE lo.inspection_datetime >= #{startDate} AND lo.inspection_datetime <= CONCAT(#{endDate}, ' 23:59:59') AND lo.ship_operator IS NOT NULL AND lo.ship_operator != ''
AND ib.码头 = #{berthingLocation}
AND lo.ship_category = #{shipCategory}
GROUP BY lo.ship_operator HAVING COUNT(*) >= 2 ORDER BY shipCount DESC
