mapper.select:
SELECT ad.dept as department, si.ship_type as shipType, COUNT(DISTINCT CONCAT(si.ship_name_cn, '_', DATE(si.inspection_date), '_', si.inspection_agency)) as inspectionCount, COALESCE(SUM(si.defect_count), 0) as defectCount, SUM(CASE WHEN si.detained = '是' THEN 1 ELSE 0 END) as detainedCount FROM ship_inspection si INNER JOIN agency_dept ad ON si.inspection_agency = ad.agency WHERE si.inspection_type = '初查'
AND si.inspection_date >= #{startDate}
AND si.inspection_date <= #{endDate}
AND ad.dept = #{department}
GROUP BY ad.dept, si.ship_type ORDER BY ad.dept, si.ship_type

SELECT ad.dept as department, COUNT(*) as onSiteCount, SUM(CASE WHEN osi.issue_found = '是' THEN 1 ELSE 0 END) as abnormalCount FROM ship_on_site_inspection osi INNER JOIN agency_dept ad ON osi.inspection_agency = ad.agency
AND osi.inspection_date >= #{startDate}
AND osi.inspection_date <= #{endDate}
AND ad.dept = #{department}
GROUP BY ad.dept ORDER BY ad.dept

SELECT ad.dept as department, COUNT(*) as pscCount, COALESCE(SUM(CAST(psc.deficiencies AS UNSIGNED)), 0) as pscDefectCount, SUM(CASE WHEN psc.detention = 'Y' THEN 1 ELSE 0 END) as pscDetentionCount FROM psc_inspection psc INNER JOIN agency_dept ad ON psc.port = ad.agency WHERE psc.type = 'INITIAL'
AND psc.inspection_date >= #{startDate}
AND psc.inspection_date <= #{endDate}
AND ad.dept = #{department}
GROUP BY ad.dept ORDER BY ad.dept
